import React from "react";
import Banner from "./components/banner";
import ItemForm from "./components/itemForm";
import { useFormHooks } from "./hook/formHooks";
import { itemHooks } from "./hook/itemHooks";

function App() {
  const { value, clearValue, changeValue, onEnterKey } = formHooks();
  const { searchValue, clearSearch, changeSearch, onEnterSearch } = formHooks();
  const {
    items,
    search,
    addItem,
    checkItem,
    removeItem,
    searchItem,
    editItem
  } = itemHooks();

  const clearFormAddItem = () => {
    addItem(value);
    clearValue();
  };

  const clearFormSearch = () => {
    searchItem(searchValue);
    clearSearch();
  };

  return (
    <div>
      <Banner searchHandler={searchItem} />
      <ItemForm />
    </div>
  );
}

export default App;

